class Solution {
public:
    bool exists(TreeNode* root, int leaf, int h) {
        h--;
        if (root) {
            // if we'd reached the leaf then it does exist
            if (h == -1) return true;

            // the hardest part is to understand how 
            // we traverse to specific leaf
            // here is my idea
            //
            //              *                 level value = 4
            //             / \
            //            /   \
            //           /     \
            //          /       \
            //         /         \
            //        /           \
            //       *             *          level value = 2
            //     /   \         /   \
            //    /     \       /     \
            //   *       *     *       *      level value = 1
            //  / \     / \   / \     / \
            // *   *   *   * *   *   *   *    level value = 0
            // 0   1   2   3 4   5   6   7

            // for instance let's have a leaf value = 6
            //
            //              6                 leaf -> 6 - 4
            //             / \                6 is enough to go right
            //            /   \
            //           /     \
            //          /       \
            //         /         \
            //        /           \
            //       *             2          leaf -> 2 - 2
            //     /   \         /   \        2 is enough to move right
            //    /     \       /     \
            //   *       *     *       0      leaf -> 0 - 1 and it is negative
            //  / \     / \   / \     / \     0 is not enough then we go left
            // *   *   *   * *   *   0   *    
            // 0   1   2   3 4   5   6   7

            if (leaf >= 1 << h) {
                leaf -= 1 << h;
                return exists(root -> right, leaf, h);
            }
            return exists(root -> left, leaf, h);
        }
        // if we reach nothing return false
        else return false;
    }

    int countNodes(TreeNode* root) {

        // if tree is empty return 0;
        if (!root) return 0;

        // let's count the tree's height
        int h = -1;
        auto n = root;
        while (n) h++, n = n -> left;

        // potentially we have leaves that
        // we can count from 0 to 2^h - 1
        // initial guess for binary search
        int l = 0, r = 1 << h;

        // bs by itself
        while (r - l > 1) {
            int mid = (l + r) / 2;

            // if leaf with number mid (starting from 0)
            // exists than we say that l will store mid value
            if (exists(root, mid, h)) l = mid;
            else r = mid;
        }

        return ((1 << h) - 1) + (l + 1);
    }
};
