class Solution:
    def countNodes(self, root: Optional[TreeNode]) -> int:
        if not root:
            return 0
        self.height = self.countHeight(root)
        if self.height == 0:
            return 1

        # binary search
        lo = 0
        hi = 2 ** self.height - 1
        lastIdx = -1
        while lo <= hi:
            mid = (lo + hi) // 2
            if self.exists(root, mid):
                lastIdx = mid
                lo = mid + 1
            else:
                hi = mid - 1

        nonLeafNodesCount = 2 ** self.height - 1
        # since node start from index 0, the amount of leaf nodes is lastIdx + 1
        return nonLeafNodesCount + lastIdx + 1

    def exists(self, root, idx):
        # first retrieve the binary representation, which is also the path 
        # from root to the leaf node
        # 0 represents going left, 1 represents going right
        path = [0] * self.height
        n = idx
        i = self.height - 1
        while i >= 0:
            path[i] = 0 if n % 2 == 0 else 1
            n = n >> 1
            i -= 1
        cur = root

        for direction in path:
            if direction == 0:
                cur = cur.left
            else:
                cur = cur.right
            if not cur:
                return False
        return True

    def countHeight(self, root):
        height = 0
        cur = root
        while cur.left:
            height += 1
            cur = cur.left
        return height     
