class Solution {
    public int countNodes(TreeNode root) {
        if (root == null)
        {
            return 0;
        }

        String binary = "1";
        int treeHeight = getLeftMostNodeHeight(root);
        while (!(root.left == null && root.right == null))
        {
            int rightsLeftMostNodeHeight = getLeftMostNodeHeight(root.right) + 1;    
            if (treeHeight == rightsLeftMostNodeHeight)
            {
                root = root.right;
                binary += 1;
            } 
            else
            {
                root = root.left;
                binary += 0;
            }

            treeHeight--;
        }

        return Integer.parseInt(binary, 2);

    }

    int getLeftMostNodeHeight(TreeNode root)
    {
        int h = 0;
        while (root != null)
        {
            root = root.left;
            h++;
        }

        return h;
    }
}
