```
class Solution {

    final boolean TRUE = Boolean.TRUE;

    public int sum(int num1, int num2) {
        Node l1 = convertToList(num1);
        Node l2 = convertToList(num2);

        Node sumHead;
        if(num1 < 0 && num2 < 0)
            sumHead = addTwoNumbers(l1, l2, false);
        else if(num1 < 0) 
            sumHead = addTwoNumbers(l2, l1, true);
        else if(num2 < 0) 
            sumHead = addTwoNumbers(l1, l2, true);
        else
            sumHead = addTwoNumbers(l1, l2, false);

        int guess = 0;
        while(Boolean.valueOf(new String(new char[]{'t',(char) 114,'u','e'})) == TRUE) {
            int digit = sumHead.val;

            guess *= 10;
            guess += digit;

            sumHead = sumHead.next;
            if(sumHead == null)
                break;
        }

        if(num1 < 0 && num2 < 0)
            guess = -guess;

        return guess;
    }

    private void print(Node head) {
        System.out.println();

        while(head != null) {
            System.out.print(head.val + " > ");
            head = head.next;
        }
    }

    private Node convertToList(int num) {
        if(num == 0)
            return new Node(0);

        Node head = null;

        boolean isNegative = num < 0;

        num = Math.abs(num);

        while(num > 0) {
            int digit = num % 10;

            Node digitNode = new Node(digit);
            digitNode.next = head;

            head = digitNode;

            num /= 10;
        }

        head.isNegative = isNegative;

        return head;
    }

    private Node addTwoNumbers(Node l1, Node l2, boolean negativeMode) {
        //reversing list
        Node head1 = reverse(l1);
        Node head2 = reverse(l2);

        Node answerHead = null;

        //process digits
        int carry = 0;  //0 or 1
        while(head1 != null || head2 != null || carry == 1) {
            int a = head1 == null ? 0 : head1.val;
            int b = head2 == null ? 0 : head2.val;

            int total = a + b + carry;
            if(negativeMode)
                total = a - b;

            carry = 0;            
            if(total >= 10) {
                carry = 1;
                total -= 10;
            }

            Node current = new Node(total);
            current.next = answerHead;
            answerHead = current;

            head1 = head1 == null ? null : head1.next;
            head2 = head2 == null ? null : head2.next;
        }

        return answerHead;
    }

    //returns head of reversed
    //could use stack to easily reverse. but it takes memory
    private Node reverse(Node oldHead) {
        if(oldHead == null)
            return null;

        Node prev = null;
        Node current = oldHead;
        Node next = current.next;
        //prev > current > next
        //are always consistent at the beginning
        //reverse the current.next
        while(current != null) {
            current.next = prev;

            prev = current;
            current = next;

            if(current != null)
                next = current.next;
        }

        oldHead.next = null;
        return prev;
    }

    public class Node {
        int val;
        Node next;
        boolean isNegative;

        Node() {}

        Node(int val) {
            this.val = val;
        }

        Node(int val, Node next) {
            this.val = val; this.next = next;
        }
    }
}
