class Solution {
public:
    struct Node {
        int val;
        Node *prev, *next;
        Node(int x) : val(x), prev(nullptr), next(nullptr) {}
    };
    int sum(int num1, int num2) {
        Node* head1 = new Node(num1), *head2 = new Node(num2);
        head1->next = head2;
        head2->prev = head1;
        int sum = 0;
        Node* curr = head1;
        while (curr != nullptr) {
            sum += curr->val;
            curr = curr->next;
        }
        return sum;
    }
};
