class Solution {
    struct TreeNode {
        int val;
        TreeNode *left, *right;
        TreeNode(int x) : val(x), left(nullptr), right(nullptr){}
    };
public:
    int sum(int num1, int num2) {
        TreeNode *root = new TreeNode(num1);
        root->right = new TreeNode(num2);
        return treeSum(root);
    }
private:
    int treeSum(TreeNode * root) {
        if(root == nullptr)
            return 0;
        return root->val + treeSum(root->right);
    }
};
