solutions.content:
I'm proudly presenting my solution running in only 2250 ms.

```
class Solution {
public:

    int sum(int A, int B) {
        //compute the sum from 1 to n
        function<int(int)> rec = [&rec](int a)->int {return a ? a + rec(a-1) : 0 ;};

        //solve the quadratic eqaution, restricted to our use
        function<int(int,int,int)> quadratic = [](int a, int b, int c)->int {return (-b + sqrt(b*b - 4*a*c)) / (2*a) ;} ;

        //find back the n from the sum
        function<int(int)> find = [&quadratic](int s)->int {return quadratic(1,1,-2*s);};

        //self explanatory 
        function<int(int)> neg = [](int a)->int {return a < 0 ? -1 : 1 ;};

        //speed up the code
        function<int(int)> speed = [&rec, &find, &neg](int a){return neg(a) * find(rec(abs(a))) ;};

        //make the average to improve our unbiased estimator
        function<int(int)> mean = [&speed](int a){
            int runs = 65 ;
            int ans = 0 ;
            for(int i = 0 ; i< runs ; ++i){
                ans += speed(a) ;
            }
            return ans / runs ;
        };

        //use the median trick
        function<int(int)> median = [&mean](int a){
            int runs = 100 ;
            vector<int> arr(runs,0) ;
            for(int i = 0 ; i< runs ; ++i){
                arr[i] = mean(a) ;
            }
            sort(arr.begin(), arr.end()) ;
            return arr[runs / 2] ;
        };
        return log(exp(median(A)) * exp(median(B)));
    }
};
```

