        //solve the quadratic eqaution, restricted to our use
        function<int(int,int,int)> quadratic = [](int a, int b, int c)->int {return (-b + sqrt(b*b - 4*a*c)) / (2*a) ;} ;

        //find back the n from the sum
        function<int(int)> find = [&quadratic](int s)->int {return quadratic(1,1,-2*s);};

        //self explanatory 
        function<int(int)> neg = [](int a)->int {return a < 0 ? -1 : 1 ;};

        //speed up the code
        function<int(int)> speed = [&rec, &find, &neg](int a){return neg(a) * find(rec(abs(a))) ;};

        //make the average to improve our unbiased estimator
        function<int(int)> mean = [&speed](int a){
