class Solution:
    def minimumWhiteTiles(self, floor: str, numCarpets: int, carpetLen: int) -> int:
        n = len(floor)
        if carpetLen*numCarpets >= n:
            return 0
        floorlist = []
        for i in floor:
            if i == '1':
                floorlist.append(1)
            else:
                floorlist.append(0)
        dp=[[0] * n for i in range(numCarpets)]

        for i in range(carpetLen, n):
            dp[0][i] = min(floorlist[i] + dp[0][i-1], sum(floorlist[:i - carpetLen + 1]))
        for j in range(1, numCarpets):
            for i in range(carpetLen * j, n):
                dp[j][i] = min(floorlist[i] + dp[j][i - 1], dp[j - 1][i - carpetLen])
        return dp[-1][-1]
