 class Solution {
    vector<int> whiteCount; // Stores no. of white tiles from ith index till the end

	// Count number of white tile from index till end 
	// Suffix sum of no. of white tiles
    void countTiles(string& floor, int n) {
        whiteCount.resize(n+2, 0);

        for(int i=n-1; i>=0; i--) {
            whiteCount[i] = whiteCount[i+1] + (floor[i] == '1');
        }
    }

    int uncoveredWhiteTiles(string& floor, vector<vector<int>>& dp, int numCarpets, int idx, int carpetLen, int n) {

		// If we reach the end, then no white is remaining
		// Assuming that all previous white tiles are covered already using carpets
        if(idx >= n) {
            return 0;
        }
		// If no carpet is available, then all the white tiles from current index
		// Till the end are uncovered. Hence return its count
        if(numCarpets == 0) {
            return whiteCount[idx];
        }

		// If we have some value for current state in DP table, then return it
        if(dp[idx][numCarpets] != -1) return dp[idx][numCarpets];

        int countWhites = n;

        // We have two choices at this point
		// Choice 1 : Use a carpet starting from current index till carpetLen
		// Choice 2 : Do no use any carpet, and move to next index. 
		// But if current tile is white then count it as uncovered
        countWhites = min(
            uncoveredWhiteTiles(floor, dp, numCarpets-1, idx+carpetLen, carpetLen, n),
            uncoveredWhiteTiles(floor, dp, numCarpets, idx+1, carpetLen, n) + (floor[idx] == '1')
        );

		// Return minimum count of uncovered white tiles using one of the two choices
        return dp[idx][numCarpets] = countWhites;
    }

public:
    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        int n = floor.size();
        countTiles(floor, n);
        vector<vector<int>> dp(n+1, vector<int>(numCarpets+1, -1));

        return uncoveredWhiteTiles(floor, dp, numCarpets, 0, carpetLen, n);
    }
};
```

