    int prefix[1001];
    int len;
    int dp[1001][1001];
    int solve(int index, int carpet) {
        if(carpet <= 0 || index >= n)
            return 0;
        if(dp[index][carpet] != -1)
            return dp[index][carpet];
        int x = 0;
        if(index)
            x = prefix[index - 1];
        int y = prefix[min(index + len - 1, n)];
        return dp[index][carpet] = max(solve(index + 1, carpet), y - x + solve(index + len, carpet - 1));
    }
    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        s = floor;
        n = s.length();
        len = carpetLen;
        memset(dp, -1, sizeof(dp));
        prefix[0] = (s[0] == '1');
        for(int i = 1; i < n; ++i)
            prefix[i] = prefix[i-1] + (s[i] == '1');
        prefix[n] = prefix[n-1];
        int ans = solve(0, numCarpets);
        return max(0, prefix[n] - ans);
    }
};

