class Solution {
    int[] suff;
    Integer[][] dp;
    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {    
        if (carpetLen == floor.length()) {
            return 0;
        }
        dp = new Integer[floor.length()][numCarpets + 1];
        //  suff[i] = number of white tiles in s[i....]
		suff = cumulative(floor);
        return helper(floor, numCarpets, carpetLen, 0);
    }

	//  normal suffix array
    public int[] cumulative(String s) {
        int[] suff = new int[s.length()];
        suff[s.length() - 1] = (s.charAt(s.length() - 1) == '0' ? 0 : 1);
        for (int i = s.length() - 2; i >= 0; i--) {
            suff[i] = suff[i + 1] + (s.charAt(i) == '0' ? 0 : 1);
        }
        return suff;
    }

    public int helper(String s, int n, int k, int idx) {

        //  no more floors remaining so no white carpets need to be covered
        if (idx >= s.length()) {
            return 0;
        }

        if (dp[idx][n] != null) {
            return dp[idx][n];
        }

        //  floors remaining but carpets exhausted
        if (n == 0) {
            return suff[idx];
        }

        //  this is already black skip this floor
        if (s.charAt(idx) == '0') {
            return helper(s, n, k, idx + 1);
        }

        //  now we have two choices whether to leave this white
        //  as it is or cover this in black


        //  if covering this and any other black floor in range as black
        int answer = helper(s, n - 1, k, idx + k);

        //  leaving this as white as it was
        answer = Math.min(answer, helper(s, n, k, idx + 1) + 1);
        return dp[idx][n] = answer;
    }
}

