class Solution {
public:
    int explore(int i,string &floor,int numCarpets, int &carpetLen, vector<vector<int>> &dp)
    {
        if(i==floor.length())
        {
            return 0;
        }
        if(dp[i][numCarpets]!=-1)
        {
            return dp[i][numCarpets];
        }

        int ans=0;
        //Put carpet here only if you have 1 or more carpet left
            if(i+carpetLen<=floor.length()&&numCarpets>0)
            {
                ans+=carpetLen+explore(i+carpetLen,floor,numCarpets-1,carpetLen,dp);
            }
            else if(numCarpets>0)
            {      //When current index+ carpet length > floor.length
                 ans+=floor.length()-i;
            }


        //Dont put carpert here
        if(floor[i]=='0')
        {   //If current carpet is black
            ans=max(ans,1+explore(i+1,floor,numCarpets,carpetLen,dp));
        }
        else
        {
            ans=max(ans,explore(i+1,floor,numCarpets,carpetLen,dp));
        }

        return dp[i][numCarpets]=ans;
    }
    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        int n=floor.length();
        //Store number of maximum black carpet from  index
        vector<vector<int>> dp(n,vector<int>(numCarpets+1,-1));
        //Explore will return maximum number of black carpet 
		//we just subtract it from floor length to get 
		//minimum number of white carpet
        return n-explore(0,floor,numCarpets,carpetLen,dp);
    }
};
