class Solution {
public:
    int solve(vector<int> &hash, int &num, int &len, int i, int j, vector<vector<int>> &dp){
        int n = hash.size();
        if (j >= num or i >= n)  return 0;
        if (dp[i][j] != -1) return dp[i][j];
//     Dont want to put the carpet on the ith place
        int not_take = solve(hash, num, len, i+1, j, dp);
//     Want to put the carpet on the ith place and the corresponding value which it gives is added
        int take = hash[i] + solve(hash, num, len, i+len, j+1, dp);

        return dp[i][j] = max(not_take, take);
    }

    int minimumWhiteTiles(string floor, int num, int len) {
        int n = floor.size();
        if (num*len >= n)   return 0;    
        int var = 0, count = 0;
        for (int i = 0;i < n;i++)   count += (floor[i] == '1');
        if (count == n){
            return (n - (num*len));
        }
//     hash stores that if we put the carpet at the ith position 
//     then how much white space i'll cover
        vector<int> hash(n);
        for (int i = n-1;i >= 0;i--){
            if (floor[i] == '1'){
                var += 1;
            }
            if (i+len < n and floor[i+len] == '1'){
                var -= 1;
            }
            hash[i] = var;
        }
//     dp[i][j] stores if i am on the ith index and jth carpet is used 
//     then how much maximum white spaces i'll cover
        vector<vector<int>> dp(n, vector<int> (num, -1));
//     solve gives me the maximus white i can cover with carpet using the num number of carpets 
//     of length len
        int temp = solve(hash, num, len, 0, 0, dp);
        return count - temp;
    }
};
```
