class Solution:
    def minimumWhiteTiles(self, floor: str, numCarpets: int, carpetLen: int) -> int:

        """
        dp(pos, numC) -> returns min number of white tiles seen from pos to the end of the floor
                         if we have numC number of carpets left

        if numC > 0, i.e. we have some carpets remaining:
            if the tile at index pos is white, then we have two choices, 
                either place a carpet from pos to pos + carpetLen - 1
                or skip this tile, so number of white tiles increases by one, and calculate the minimum answer from index
                pos + 1
            else
                no need to cover this tile, we can move ahead
        else:
            we cannot cover any more white tiles
            so return number of white tiles from pos to the end of floor


        time complexity : O(numCarpets * len(floor)) ~ O(n^2)
        """

        @lru_cache(None) 
        def dp(pos, numC):
            if pos >= n:
                return 0

            if numC > 0:
                if floor[pos] == "1":
                    return min(1 + dp(pos + 1, numC), dp(pos + carpetLen, numC - 1))
                else:
                    return dp(pos + 1, numC)
            else:
                return ones[pos]


        n = len(floor)

        #ones[i] stores the number of white tiles from index i to the end of floor 
        ones = [0 for i in range(n)]
        ones[-1] = (floor[-1] == "1")

        for i in range(n-2, -1, -1):
            ones[i] = ones[i+1] + (floor[i] == "1")


        return dp(0, numCarpets)



