/**
 * the dynamic programming solution is employed.
 *
 * `dp[carpets][tiles]` stands for the minimum number of while tiles
 * when `carpets` carpets have been covered in the tile range [0, tiles),
 * 0 inclusive, `tiles` exclusive.
 * where `carpets` is in the range [0, `numCarpets`], both inclusive
 *       `tiles` is in the range [0, `floor.size()`], both inclusive
 *
 * initial:
 * if `carpets` == 0
 * 1. if tiles == 0,
 *    dp[carpets][tiles] = 0
 * 2. if tiles > 0,
 *    dp[carpets][tiles] = dp[carpets][tiles - 1] + (floor[tiles - 1] == '1' ? 1 : 0)
 *
 * induction:
 * 1. if tiles == 0
 *    dp[carpets][tiles] = 0
 * 2. otherwise
 * 2.1. let `covered` stand for the minimum number of while tiles
 *      if the `tiles - 1`'th tile is covered by a carpet
 *      covered = dp[carpets - 1][max(0, tiles - carpetLen)]
 * 2.2. let `uncovered` stand for the minimum number of while tiles
 *      if the `tiles - 1`'th tile is not covered by a carpet,
 *      uncovered = dp[carpets][tiles - 1] + (floor[tiles - 1] == '1' ? 1 : 0)
 * dp[carpets][tiles] = min(covered, uncovered)
 *
 * target:
 * dp[numCarpets][floor.size()]
 *
 * Time Complexity: O(numCarpets * n)
 * Space Complexity: O(n)
 * where `n` is the length of the string `floor`
 */
class Solution {
 public:
  int minimumWhiteTiles(const string &floor, const int numCarpets, const int carpetLen) {
    constexpr int range = 2;
    constexpr char white = '1';
    const int n = static_cast<int>(floor.size());
    int dp[range][n + 1];
    memset(dp, 0, sizeof(dp));
    int previous = 0;
    int current = 1;
    for (int tiles = 1; tiles < n + 1; ++tiles) {
      dp[previous][tiles] = dp[previous][tiles - 1] + (floor[tiles - 1] == white ? 1 : 0);
    }

    for (int carpets = 1; carpets < numCarpets + 1; ++carpets) {
      for (int tiles = 1; tiles < n + 1; ++tiles) {
        const int covered = dp[previous][max(0, tiles - carpetLen)];
        const int uncovered = dp[current][tiles - 1] + (floor[tiles - 1] == white ? 1 : 0);
        dp[current][tiles] = min(covered, uncovered);
      }

      previous ^= 1;
      current ^= 1;
      memset(dp[current], 0, sizeof(dp[current]));
    }

    return dp[previous][n];
  }
};
```

