## Code
```C++ []
class Solution {
public:

    #define WHITE '1'

    vector<vector<int>> dp;
    int n, c;

    int solve(string &floor, int ind, int rem){

        // Reached end - No white tiles visible
        if(ind >= n) return 0;

        // Checking cache
        if(dp[ind][rem] != -1) return dp[ind][rem];

        int res = 1e8;

        // We have 2 options -> Cover this tile or don't

        // Don't cover this tile 
        // If its a white tile, one white tile is visible already
        res = (floor[ind] == WHITE) + solve(floor, ind + 1, rem);

        // Cover this tile if its a white tile and carpets are still remaining
        if(floor[ind] == WHITE and rem) res = min(res, solve(floor, ind + c, rem - 1));

        // Caching
        return dp[ind][rem] = res;

    }

    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        n = floor.length(), c = carpetLen;

        // 2D DP {index, carpetsRemaining}
        dp = vector<vector<int>> (n + 1, vector<int> (numCarpets + 1, -1));

        return solve(floor, 0, numCarpets);
    }
};
```
```Java []
class Solution {

    private static final char WHITE = '1';
    private int[][] dp;
    private int n, c;

    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        n = floor.length();
        c = carpetLen;

        // 2D DP {index, carpetsRemaining}
        dp = new int[n + 1][numCarpets + 1];
        for (int[] row : dp) {
            Arrays.fill(row, -1);
        }

        return solve(floor, 0, numCarpets);
    }

    private int solve(String floor, int ind, int rem) {
        // Reached end - No white tiles visible
        if (ind >= n) {
            return 0;
        }

        // Checking cache
        if (dp[ind][rem] != -1) {
            return dp[ind][rem];
        }

        int res = (int) 1e8;

        // We have 2 options -> Cover this tile or don't

        // Don't cover this tile
        // If it's a white tile, one white tile is visible already
        res = (floor.charAt(ind) == WHITE ? 1 : 0) + solve(floor, ind + 1, rem);

        // Cover this tile if it's a white tile and carpets are still remaining
        if (floor.charAt(ind) == WHITE && rem > 0) {
            res = Math.min(res, solve(floor, ind + c, rem - 1));
        }

        // Caching
        dp[ind][rem] = res;
        return res;
    }
}

