class Solution:
    def minimumWhiteTiles(self, floor: str, nc: int, cl: int) -> int:

        n=len(floor)
        total=sum(int(ch) for ch in floor)

        @cache
        def f(ind,count):
            if ind<0 or count==0:
                return 0

            ans=floor[max(0,ind-cl+1):ind+1].count("1") + f(ind-cl,count-1)
            ans=max(ans,f(ind-1,count))

            return ans

        return total-f(n-1,nc)
