import heapq
class Solution:
    def minimumWhiteTiles(self, floor: str, numCarpets: int, carpetLen: int) -> int:

        memo = [[None] * numCarpets for _ in range(len(floor))]
        prefix = [0] * (len(floor) + 1)
        for i, val in enumerate(floor):
            prefix[i + 1] = int(val) + prefix[i]

        def helper(i, k):
            nonlocal floor, numCarpets, carpetLen, prefix, memo

            if i == len(floor) or k == -1:
                return 0
            if memo[i][k] is not None:
                return memo[i][k]

            right = min(i + carpetLen, len(floor))
            covered = prefix[right] - prefix[i] + helper(right, k - 1)
            not_covered = helper(i + 1, k)
            memo[i][k] = max(covered,  not_covered)

            return memo[i][k]

        return prefix[-1] - helper(0, numCarpets - 1)
```

