import scala.collection.mutable.HashMap
object Solution {
    def minimumWhiteTiles(floor: String, numCarpets: Int, carpetLen: Int): Int = {
        def mem[I, O](f: I => O) = new HashMap[I, O]() {
            override def apply(key: I) = getOrElseUpdate(key, f(key))
        }

        lazy val dp: ((Int, Int)) => Int = mem {
            case (i, _) if (i < 0) => 0
            case (i, 0) => floor.take(i + 1).count(_ == '1')
            case (i, j) => dp(i - carpetLen, j - 1) min dp(i - 1, j) + floor(i) - '0'
        }

        dp(floor.size - 1, numCarpets)
    }
}
