class Solution {
    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        if(numCarpets * carpetLen >= floor.length()) return 0;
        int[][] dp = new int[numCarpets+1][floor.length()];
        dp[0][0] = floor.charAt(0) == '0' ? 0 : 1;
        for(int i=1;i<floor.length();i++) dp[0][i] = dp[0][i-1] + (floor.charAt(i) == '0' ? 0 : 1);

        for(int i=1;i<=numCarpets;i++) {
            for(int j=carpetLen;j<floor.length();j++) {
                if(floor.charAt(j) == '0') {
                    dp[i][j] = dp[i][j-1];
                } else {
                    dp[i][j] = Math.min(1 + dp[i][j-1], dp[i-1][j-carpetLen]);
                }
            }
        }
        return dp[numCarpets][floor.length()-1];
    }
    // 1 1 2 3 3 4 4 5
    // 0 0 1 1 1 2 2 3
    // 0 0 0 0 0 1 1 2
}
