def minimumWhiteTiles(self, floor, numCarpets, carpetLen):
    m, n = numCarpets + 2, len(floor) + carpetLen
    dp, A = [[[0, 1000][not i]] * n for i in range(m)], list(map(int, floor))
    for i in range(1, m):
        for j in range(carpetLen, n):
            dp[i][j] = min(dp[i - 1][j - carpetLen], dp[i][j - 1] + A[j - carpetLen])
    return dp[-1][-1]
