class Solution {

    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {

        char [] tiles = floor.toCharArray();
        int n = tiles.length;

        int [] dp = new int[n+1];

        int whiteTiles = 0;
        for(int i = 1; i<=n; i++){
            if(tiles[i-1]=='1') whiteTiles++;
            dp[i] = whiteTiles;
        }

        for(int i = 1; i<=numCarpets; i++){
            int [] temp = new int[n+1];
            for(int j = 1; j<=n; j++){
                temp[j] = tiles[j-1]=='0' ? temp[j-1] : Math.min(dp[Math.max(j-carpetLen, 0)], 1+temp[j-1]);
            }
            dp = temp;
        }

        return dp[n];
    }
}
