class Solution {
public:
    int minimumWhiteTiles(
        string floor, 
        int n,
        int k
    ) {
        int m = floor.size();
        if (n * k >= m) return 0;

        vector<int> ps(m+1);
        for (int i = 1; i <= m; ++i)
            ps[i] = ps[i-1] + (floor[i-1] == '1');

        vector<int> dp, dq;
        dp = ps;
        for (int i = 1; i <= n; ++i) {
            dq.assign(m+1, 0);
            for (int j = i*k+1; j <= m; ++j) {
                dq[j] = min(
                    dq[j-1]+(floor[j-1]=='1'),
                    dp[j-k]
                );
            }
            dp = dq;
        }

        return dp[m];
    }
};
