class Solution {
public:
    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        int i, j;
        const int inf = 1001;
        int len = floor.length();
        vector<vector<int>> dp(len + 1, vector<int> (numCarpets + 1, inf));

        // Base Cases
        for(i = 0; i <= numCarpets; i++){
            dp[0][i] = 0;
        }

        for(i = 1; i <= len; i++){
            dp[i][0] = dp[i - 1][0] + (floor[i - 1] == '1' ? 1 : 0);
        }

        for(i = 1; i <= len; i++){
            for(j = 1; j <= numCarpets; j++){
                int isWhiteTile = floor[i - 1] == '1' ? 1 : 0;

                dp[i][j] = dp[i - 1][j] + isWhiteTile;

                if(i - carpetLen >= 0){
                    dp[i][j] = min(dp[i][j], dp[i - carpetLen][j - 1]);
                }
                else{
                    dp[i][j] = min(dp[i][j], dp[0][j - 1]);
                }
            }
        }

        return dp[len][numCarpets];
    }
};
