func minimumWhiteTiles(floor string, numCarpets int, carpetLen int) int {
    memo := make([][]int, len(floor))
    for i := 0; i < len(memo); i++ {
        memo[i] = make([]int, numCarpets + 1)
        for j := 0; j < len(memo[i]); j++ {
            memo[i][j] = -1
        }
    }
    return dfs(floor, numCarpets, carpetLen, 0, memo)
}

func dfs(floor string, carp int, size int, pos int, memo [][]int) int {
    if pos >= len(floor) {
        return 0
    }

    if memo[pos][carp] != -1 {
        return memo[pos][carp]
    }

    res := 0
    if floor[pos] == '0' {
        res = dfs(floor, carp, size, pos + 1, memo)
    } else {
        res = 1 + dfs(floor, carp, size, pos + 1, memo)
        if carp > 0 {
            res = min(res, dfs(floor, carp - 1, size, pos + size, memo))
        }
    }  

    memo[pos][carp] = res

    return res
}

func min(a int, b int) int {
    if a < b {
        return a
    }
    return b
}
