class Solution:
    def minimumWhiteTiles(self, floor: str, n: int, l: int) -> int:
        m = len(floor)

        @lru_cache(None)
        def dp(i, used):
            if i >= m:
                return 0
            if used < 1:
                return dp(i + 1, used) + (1 if floor[i] == '1' else 0)
            if used * l >= m - i:
                return 0
            if floor[i] == '0':
                return dp(i + 1, used)
            return min(dp(i + l, used - 1), dp(i + 1, used) + 1)

        res = dp(0, n)
        dp.cache_clear()
        return res
