class Solution {
public:
    const int INF = 1e9 + 7;
    int minimumWhiteTiles(string floor, int numCarpets, int carpetLen) {
        int n = floor.size();
        if (numCarpets * carpetLen >= n) return 0;
        vector<vector<int>> dp(n + 1, vector<int> (numCarpets + 1, INF));
        int white = 0;
        for (int i = 0; i < n; i++) {
            if (floor[i] == '1') {
                ++white;
            }
            dp[i + 1][0] = white;
        }
        dp[0][0] = 0;
        for (int i = 0; i < n; i++) {
            for (int k = 0; k <= numCarpets; k++) {
                if (floor[i] == '0') {
                    dp[i + 1][k] = min(dp[i + 1][k], dp[i][k]);
                }
                else {
                    dp[i + 1][k] = min(dp[i + 1][k], dp[i][k] + 1);
                }
                if (i + carpetLen <= n && k + 1 <= numCarpets) {
                    dp[i + carpetLen][k + 1] = min(dp[i + carpetLen][k + 1], dp[i][k]);
                }
            }
        }
        return dp[n][numCarpets];
    }
};
