	class Solution {

    void fillPrefix(vector<int> &pre, string &s, int n) {

        int p = 0;
        for(int i = 0; i < n; i++) {
            if(s[i] == '1')
                p++;

            pre[i] = p;
        }
    }

    int memo(int i, int used, string &s, int n, int nc, int l, vector<vector<int>> &dp, vector<int> &pre) {

        if(i >= n || nc == used)
            return 0;

        if(dp[i][used] != -1)
            return dp[i][used];

        if(s[i] == '0')
            dp[i][used] = memo(i + 1, used, s, n, nc, l, dp, pre);
        else {
            int ind = min(i + l - 1, n - 1);
            int ans = pre[ind];
            if(i > 0)
                ans -= pre[i - 1];

            int include = memo(i + l, used + 1, s, n, nc, l, dp, pre);
            int exclude = memo(i + 1, used, s, n, nc, l, dp, pre);

            dp[i][used] = max(include + ans, exclude);
        }

        return dp[i][used];
    }

	public:
    int minimumWhiteTiles(string s, int nc, int l) {

        int n = s.length();
        vector<int> pre(n, 0);
        fillPrefix(pre, s, n);

        if(pre[n - 1] == 0)
            return 0;

        vector<vector<int>> dp(n, vector<int> (nc, -1));
        return pre[n - 1] - memo(0, 0, s, n, nc, l, dp, pre);
    }
	};

