class Solution {
    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        char [] arr = floor.toCharArray();
        int [] pre = new int[arr.length];
        pre[0] = (arr[0]=='1'?1:0);
        for(int i = 1;i<arr.length;i++){
            if(arr[i]=='1'){
                pre[i] = (pre[i-1]+1);
            }else{
                pre[i] = pre[i-1];
            }
        }
        int [][] dp = new int[pre.length+1][numCarpets+1];
        for(int [] row : dp){
            Arrays.fill(row,-1);
        }
        return pre[pre.length-1]-solve(pre,0,numCarpets,carpetLen,dp);
    }
    private int solve(int [] pre,int idx,int num,int carpetLen,int[][] dp){
        if(idx>=pre.length){
            return 0;
        }
        if(num<=0){
            return 0;
        }
        if(dp[idx][num]!=-1){
            return dp[idx][num];
        }
        int end = (idx+carpetLen-1>=pre.length)?pre.length-1:(idx+carpetLen-1);
        // place carpet at idx.
        int best = getSum(pre,idx,end) + solve(pre,end+1,num-1,carpetLen,dp);
        //dont place carpet at idx.
        best = Math.max(solve(pre,idx+1,num,carpetLen,dp),best);

        return dp[idx][num]=best;
    }

    private int getSum(int [] pre,int start,int end){
        if(start>0){
            return pre[end] - pre[start-1];
        }
        return pre[end];
    }
}
