class Solution {

    public int minimumWhiteTiles(String floor, int numCarpets, int carpetLen) {
        int countWhite = 0;
        char[] floorArr = floor.toCharArray();
        for(char c : floorArr) {
            if (c == '1') countWhite++;
        }

        int numOfWhiteSoFar = 0;
        int[] rangeWhite = new int[floorArr.length];
        for(int i = 0; i < floorArr.length; i++) {
            if (floorArr[i] == '1') numOfWhiteSoFar++;
            rangeWhite[i] = numOfWhiteSoFar;
        }

        Integer[][] cache = new Integer[numCarpets + 1][floorArr.length + 1];
        int max= maximumWhiteTiles(floorArr, numCarpets, carpetLen, 0, cache, rangeWhite);
        return countWhite - max;
    }

    private int maximumWhiteTiles(char[] floorArr, int numCarpets, int carpetLen, int start
                                 , Integer[][] cache, int[] rangeWhite) {

        // start from a index what is maximum white tiles you could cover;
        if (numCarpets == 0) return 0;
        if (start >= floorArr.length) return 0;

        if (cache[numCarpets][start] != null) return cache[numCarpets][start];
