impl Solution {
    pub fn find_closest_number(nums: Vec<i32>) -> i32 {
        use std::cmp::Ordering;
        nums.iter()
            .fold((i32::MIN, i32::MAX), |(val, dist), &x| {
                match dist.cmp(&(x.abs())) {
