class Solution {
    public int findClosestNumber(int[] nums) {
      int minDis=0;
      int check=Integer.MAX_VALUE;
      for(int i=0;i<nums.length;i++){

          int dis=Math.abs(nums[i]);

          if(nums[i]>=0 && dis<=check){
              check=nums[i];
              minDis=nums[i];
          }
          else if(nums[i]<0 && dis<check){
              check=-nums[i];
              minDis=nums[i];
          }
      }
        return minDis;
    }
}
