    public int findClosestNumber(int[] nums) {
        int min = Integer.MAX_VALUE, closest = 0;
        for(int num : nums){
            if(num == 0){ return 0; }
            int diff = Math.abs(num);
            if(min > diff){
                min = diff;
                closest = num;   
            }else if(min == diff){
                closest = Math.max(num, closest); 
            }
        }
        return closest;
    }
