type NumWithDistanceEntry = [number, number];

function findClosestNumber(nums: number[]): number {
  const numsWithDistances: { [key: string | number]: number } = {};

  nums.forEach((num) => {
    numsWithDistances[num] = Math.abs(0 - num);
  });

  const entriesOfNumsWithDistances: NumWithDistanceEntry[] = Object.entries(
    numsWithDistances
  ).map((numWithDistance) => [Number(numWithDistance[0]), numWithDistance[1]]);

  const sortedEntries = entriesOfNumsWithDistances.sort(
    (a: NumWithDistanceEntry, b: NumWithDistanceEntry) => {
      if (a[1] === b[1]) {
        if (a[0] < b[0]) return 1;
        if (a[0] > b[0]) return -1;
        return 0;
      } else {
        return a[1] - b[1];
      }
    }
  );

  return sortedEntries[0][0];
}
```

