var findClosestNumber = function(nums) {
  let plus = [];
  let minus = [];
  let sP = [];
  let sM = [];

  if (nums == 0) {
    return 0;
  } else {
    for (let i = 0; i < nums.length; i++) {
      if (nums[i] > 0) {
        plus.push(nums[i]);
      } else {
        minus.push(nums[i]);
      }
    }

    sP = plus.sort((a, b) => a - b);
    sM = minus.sort((a, b) => b - a);

    if (sP.length === 0) {
      return sM[0];
    } else if (sM.length === 0) {
      return sP[0];
    } else if (Math.abs(sP[0]) > Math.abs(sM[0])) {
      return sM[0];
    } else {
      return sP[0];
    }
  }
};

