function findClosestNumber(nums: number[]): number {
    let closest = nums[0]
    let closestDistance = Math.abs(nums[0] - 0)

    let closestPositive = null
    let closestNegative = null

    for(let i = 1; i <= nums.length; i++){
        // returns distance in positive to caluclate closest
        let distance = Math.abs(nums[i] - 0)

        // calculate the distance
        if(distance < closestDistance){
            closestDistance = distance
            closest = nums[i]
        }

        if(nums[i] < 0 && (nums[i] < closestNegative) || closestNegative === null ){ // negative number
            closestNegative = nums[i]
        }else if (nums[i] > 0 && (nums[i] < closestPositive || closestPositive === null)){ // positive number
            closestPositive = nums[i]
        }

    }

    // check if positive and negative number is same
    if(closestPositive && closestNegative){
        if(Math.abs(closestNegative) === closestPositive){
            return closest = closestPositive
        }
    }

    return closest
};
