class Solution {
public:
    ListNode* swapPairs(ListNode* head) {
        if(head == NULL || head->next == NULL){  // If List is empty otr has only 1 node return;
            return head;
        }

        ListNode* first = head; // first pointer to List's first node
        ListNode* second = head->next; // second pointer to List's second node
        ListNode* third = head->next->next; // third pointer to List's third node

        second->next = first; // Made second's next point to first instead of third. So, it's like first's next and second's next are pointing to second and first respectively.

        first->next = swapPairs(third); // made first's next point to swapPairs(third), because we assume, recursion will swap the rest of the pairs and whatever it will return first's next will point to that node.

        return second;
    }
};
