class Solution {
    fun swapPairs(head: ListNode?): ListNode? {

		// if we got empty list return null
        head ?: return null

		// create a pointer, pointing to second element in the list 
		// we will handle the case of swapping first two elements
		// in the list later
        var ptr: ListNode? = head?.next

        // in case of list with single element, return the list as
		// swapping is not possible with just one element
        ptr ?: return head

        while (ptr?.next != null) {
			// assign a temp pointer pointing to first of the two elements
			// to be swapped (we're swapping in chunks on two in entire list)
