        pair<int,int> right=helper(root->right);
        int include=max(max(root->val,root->val+left.first+right.first),max(root->val+left.first,root->val+right.first)); 
        int notinclude=max(left.second,right.second);
        pair<int,int>ans;
        ans.second=max(include,notinclude);
        ans.first=max(max(left.first,right.first)+root->val,root->val);
        return ans;
    }
    int maxPathSum(TreeNode* root) {
        return helper(root).second;
    }
};

