class Solution {
    func maxPathSum(_ root: TreeNode?) -> Int {
        var result: Int = 0;
        if root != nil {
            result = root!.val
            maxPathSumHelper(root, &result)
        }
        return result
    }

    func maxPathSumHelper(_ root: TreeNode?, _ result: inout Int) -> Int {

        guard let root = root else { return 0 }

        var leftCost = maxPathSumHelper(root.left, &result)
        if leftCost < 0 { leftCost = 0 }

        var rightCost = maxPathSumHelper(root.right, &result)
        if rightCost < 0 { rightCost = 0 }

        var val = root.val + leftCost + rightCost
        if val > result { result = val }

        return root.val + max(leftCost, rightCost)
    }

}
```

