class Solution {
private:
    // Operator precedence.
    static int prec(char ch) {
        switch (ch) {
            case '(':
                // Use the lowest because we handled this differently.
                return -1;

            case '+':
            case '-':
                return 0;

            case '*':
            case '/':
                return 1;

            case '_':
                // Use '_' for unary minus.
                return 2;
        }
        assert(false);
        return -1;
    }

    // Apply operators.
    static void apply(stack<int>& out, char op) {
        // Unary minus.
        if (op == '_') {
            out.top() *= -1;
            return;
        }

        const int right = out.top(); out.pop();
        const int left = out.top(); out.pop();
        switch (op) {
            case '+':
                out.push(left + right);
                break;
            case '-':
                out.push(left - right);
                break;
            case '*':
                out.push(left * right);
                break;
            case '/':
                out.push(left / right);
                break;
            default:
                assert(false);
        }
    }

