class Solution:
def calculate(self, s):
    s = '+(+' + s + ')'
    s = s.replace('+-', '-').replace('++', '+') # for the corner case '-5', '+5'
    stack = []
    for i in s:
        if i == ')':
            total = 0
            while stack[-1] != '(':
                total += int(stack.pop())
            stack.pop()
            sign = 1 if stack.pop() == '+' else -1
            stack.append(sign * total)
        elif i.isdigit() and stack[-1][-1] in '+-0123456789':
            stack[-1] += i
        elif i != ' ':
            stack.append(i)
    return stack[0]

