class Solution {
    public int calculate(String s) {
        int n = s.length();
        int sum = 0;
        int sign = 1;
        Stack<Integer> st = new Stack<Integer>();

        for(int i=0; i<n; i++)
        {
             // number
            if(s.charAt(i)>='0' &&  s.charAt(i)<='9')
            {
                int num = 0;
                while(i<n && s.charAt(i)>='0' && s.charAt(i)<='9' )    //Eg: 125  (it will work for single and multiple digit number )
                {
                    num =num*10 + (s.charAt(i)-'0') ;    //we are converting Character to Integer by subtracting ASCII value of Char '0'
                    i++;
                }
                sum = sum + num*sign;    // eg: sum = sum + 125*(+1) =125
                i--;                    // we are doing  i-- becoz in last step we have gone to next value by incrementing i.e i++
            }

            else if(s.charAt(i)=='+'){
                sign = 1;
            }

            else if(s.charAt(i)=='-'){  
                sign = -1;
            }

            else if(s.charAt(i)=='('){     // when there is opening bracket - push SUM in stack , and Push sign(+ve,-ve) in Stack . NOTE: For +ve ( we use +1 ) , for -ve (we use -1)
                st.push(sum);
                st.push(sign);
                sum=0;
                sign=1;
            }

            else if(s.charAt(i)==')') {          // when there is closing bracket- 1st pop SIGN (1 or -1 ) using st.pop(). Secondly pop Number that is present .
                sum = sum*st.pop();           //multiply number with sign -1 or +1
                sum = sum + st.pop();
            }

        }//forLoop_End

        return sum;
    }
}
