    	{
        	int len = s.length();
        	int sum = 0;
        	bool presign = true, sign = true;
        	stack<int> stk;

        	for(int ii=0; ii<len; ++ii)
        	{
        		char cc = s[ii];

        		if(' ' == cc)
        			continue;
        		if('+' == cc)
        			sign = true;
