    int calculate(string s) {
        stack<char> st;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]!=' '){
                if(st.empty()){
                    st.push(s[i]);
                }else{
                    if(s[i]=='('){

                        int bracket_evaluation=0;


                        while(st.top()!=')'){
                            int symbol=1;
                            int d=0;
                            if(st.top()=='-'){

                                //removing symbol
                                st.pop();
                                symbol=-1;

                            }else{

