class Solution {
    public int calculate(String s) {
        if (s == null || s.length() == 0) {
            return 0;
        }

        Stack<Integer> numStack = new Stack<>();
        Stack<Integer> signStack = new Stack<>();

        int result = 0;
        int sign = 1;

        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);

            if (c == '-') {
                sign = -1;
                continue;
            }

            if (c == '+' || c == ' ') {
                continue;
            }

            if (c == '(') {
                numStack.push(result);
                result = 0;
                signStack.push(sign);
                sign = 1;
                continue;
            }

            if (c == ')') {
                result = signStack.pop() * result + numStack.pop();
                sign = 1;
                continue;
            }

            int num = 0;
            while (i < s.length() && Character.isDigit(s.charAt(i))) {
                num = num * 10 + (s.charAt(i) - '0');
                i++;
            }
            i--;
            result += sign * num;
            sign = 1;
        }

        return result;
    }
}
