	// sum to calculate the total result of evaluation
         int sum=0;
  // sign to tell whether its going to be positive or negative		 
        int sign=1;
        stack<int> st;
        for(int i=0;i<s.size();i++){
            // if the current s[i] is digit
            if(isdigit(s[i])){
	    // value to get the value of  multiple digits  like
        // 3124+1 will give 3124 it will end loop at + so we do i-- to  go back 
      // one step to not miss this
                int val=0;
                while(i<s.size()&&isdigit(s[i])){
                    val=val*10+(s[i]-'0');
                    i++;
                }
                i--;
		// to get the sign of val, mulitply it with sign and then reset the sign for next ones
                val=val*sign;
                sign=1;
		// add the val to our sum
                sum+=val;
            }

            else if(s[i]=='('){
	// whenever we encounter an '(' push the sum and sign to stack and reset them		
                st.push(sum);
                st.push(sign);
                sum=0;
                sign=+1;
            }

            else if(s[i]==')'){
	// when we get ')' this means an end of parantheses  so multiply with stack top
	// which will give us the sign of sum and pop it
                sum*=st.top();
                st.pop();
// after getting the sum add it with our previous sum stored in stack to get the total resultant
// and pop it after that
                sum+=st.top();
                st.pop();
            }

            else if(s[i]=='-'){
			// if we encounter a - sign just mulitply it our sign
                sign*=-1;
            }
        }
        // return our total sum or evaluation
        return sum;
    }
};

