class Solution {
public:
    vector<string> cellsInRange(string s) 
    {
        char ch1 = s[0], ch2 = s[3];              // stroing 0th and 3rd char [A1:B2] , 0th=A , 3rd=B
        int in1= s[1]-'0', in2=s[4]-'0';          // stroing 1st and 4th int [A1:B2] ,  1st=1 , 4th=2
        vector<string> ans;

        for(int i=ch1-'A'; i<=ch2-'A'; i++)
        {
            for(int j=in1; j<=in2; j++)
            {
                string s= ch1+to_string(j);
                ans.push_back(s);
            }
            ch1 = ch1+1;
        }
        return ans;  
    }
};
