    public List<String> cellsInRange(String s) {
        List<String> ans=new ArrayList<>();

        String[]arr=s.split(":");
        char sc=arr[0].charAt(0);
        int sr=Integer.parseInt(arr[0].substring(1));

        char ec=arr[1].charAt(0);
        int er=Integer.parseInt(arr[1].substring(1));

        for(int i=sc;i<=ec;i++){
            getAll(sr,i,er,ec,ans);
        }
        return ans;
